s = off","CREATE OR REPLACE FUNCTION public.add_admin_user()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\nbegin\n  if new.role in ('super_admin', 'admin') and (old.role is null or old.role not in ('super_admin', 'admin')) then\n    insert into public.admin_users (user_id)\n    values (new.user_id)\n    on conflict (user_id) do nothing;\n  end if;\n  return new;\nend;\n$function$","CREATE OR REPLACE FUNCTION public.add_to_admin_users_on_admin_role()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\nbegin\n  -- Check if the inserted role_id matches an admin role\n  if exists (\n    select 1 from public.roles r\n    where r.id = new.role_id and lower(r.name) in ('admin', 'super_admin')\n  ) then\n    -- Insert into admin_users if not already present\n    insert into public.admin_users (id, email, role, created_at, updated_at)\n    select p.user_id, p.email, r.name, now(), now()\n    from public.profiles p\n    join public.roles r on r.id = new.role_id\n    where p.user_id = new.user_id\n    on conflict (id) do nothing;\n  end if;\n  return new;\nend;\n$function$","create or replace view \\"public\\".\\"admin_assignment_roster_v\\" as  SELECT ab.assignment_id,\n    b.booking_id,\n    b.user_id AS member_id,\n    ((b.first_name || ' '::text) || b.last_name) AS full_name,\n    b.email,\n    att.status,\n    att.marked_at,\n    att.marked_by\n   FROM ((assignment_bookings ab\n     JOIN bookings b ON ((b.booking_id = ab.booking_id)))\n     LEFT JOIN class_attendance att ON (((att.assignment_id = ab.assignment_id) AND (att.member_id = b.user_id))))","create or replace view \\"public\\".\\"admin_class_overview_v\\" as  SELECT ca.id AS assignment_id,\n    ca.instructor_id,\n    ca.date,\n    ca.start_time,\n    ca.end_time,\n    ca.class_status,\n    ca.payment_status,\n    COALESCE(ca.override_payment_amount, ca.payment_amount) AS final_payment_amount,\n    ct.name AS class_type_name,\n    ct.description AS class_type_description,\n    ct.difficulty_level AS class_type_difficulty,\n    ct.duration_minutes AS class_type_duration,\n    ca.schedule_type,\n    ca.timezone,\n    count(att.id) FILTER (WHERE (att.status = ANY (ARRAY['present'::attendance_status_enum, 'late'::attendance_status_enum, 'makeup_completed'::attendance_status_enum]))) AS attended_count,\n    count(att.id) FILTER (WHERE (att.status = 'no_show'::attendance_status_enum)) AS no_show_count,\n    count(att.id) FILTER (WHERE (att.status = ANY (ARRAY['absent_excused'::attendance_status_enum, 'absent_unexcused'::attendance_status_enum]))) AS absent_count,\n    avg(cr.rating) AS avg_rating,\n    count(cr.id) AS ratings_submitted\n   FROM (((class_assignments ca\n     LEFT JOIN class_types ct ON ((ca.class_type_id = ct.id)))\n     LEFT JOIN class_attendance att ON ((att.assignment_id = ca.id)))\n     LEFT JOIN class_ratings cr ON ((cr.assignment_id = ca.id)))\n  GROUP BY ca.id, ct.name, ct.description, ct.difficulty_level, ct.duration_minutes","CREATE OR REPLACE FUNCTION public.admin_update_user_roles(target_user_id uuid, new_role_names text[], requesting_user_id uuid)\n RETURNS json\n LANGUAGE plpgsql\n SECURITY DEFINER\n SET search_path TO 'public'\nAS $function$\r\nDECLARE\r\n  role_id_var UUID;\r\n  role_name TEXT;\r\n  admin_profile RECORD;\r\n  result_json JSON;\r\nBEGIN\r\n  -- Debug: Log the requesting user ID\r\n  RAISE NOTICE 'Checking admin permissions for user: %', requesting_user_id;\r\n  \r\n  -- Check if the requesting user exists and is an admin or super_admin\r\n  SELECT user_id, role, is_active INTO admin_profile\r\n  FROM profiles \r\n  WHERE user_id = requesting_user_id;\r\n  \r\n  -- Debug: Log what we found\r\n  RAISE NOTICE 'Found profile: user_id=%, role=%, is_active=%', \r\n    admin_profile.user_id, admin_profile.role, admin_profile.is_active;\r\n  \r\n  -- Check if user exists\r\n  IF admin_profile.user_id IS NULL THEN\r\n    RAISE EXCEPTION 'User profile not found for user_id: %', requesting_user_id;\r\n  END IF;\r\n  \r\n  -- Check if user is active\r\n  IF admin_profile.is_active IS FALSE THEN\r\n    RAISE EXCEPTION 'User account is not active';\r\n  END IF;\r\n  \r\n  -- Check if user has admin or super_admin role\r\n  IF admin_profile.role NOT IN ('admin', 'super_admin') THEN\r\n    RAISE EXCEPTION 'Insufficient permissions: Admin or Super Admin role required. Current role: %', \r\n      COALESCE(admin_profile.role, 'NULL');\r\n  END IF;\r\n  \r\n  RAISE NOTICE 'Admin check passed. Updating roles for user: %', target_user_id;\r\n  \r\n  -- Delete existing roles for the target user\r\n  DELETE FROM user_roles WHERE user_id = target_user_id;\r\n  RAISE NOTICE 'Deleted existing roles for user: %', target_user_id;\r\n  \r\n  -- Insert new roles\r\n  FOREACH role_name IN ARRAY new_role_names\r\n  LOOP\r\n    -- Get the role ID for this role name\r\n    SELECT id INTO role_id_var \r\n    FROM roles \r\n    WHERE name = role_name;\r\n    \r\n    RAISE NOTICE 'Processing role: %, found role_id: %', role_name, role_id_var;\r\n    \r\n    -- If role exists, insert the user_role record\r\n    IF role_id_var IS NOT NULL THEN\r\n      INSERT INTO user_roles (user_id, role_id, assigned_by, assigned_at)\r\n      VALUES (target_user_id, role_id_var, requesting_user_id, NOW());\r\n      \r\n      RAISE NOTICE 'Inserted role: % for user: %', role_name, target_user_id;\r\n    ELSE\r\n      RAISE NOTICE 'Role not found: %', role_name;\r\n    END IF;\r\n  END LOOP;\r\n  \r\n  -- Return success response\r\n  SELECT json_build_object(\r\n    'success', true,\r\n    'message', 'User roles updated successfully',\r\n    'user_id', target_user_id,\r\n    'new_roles', new_role_names,\r\n    'requesting_user', requesting_user_id\r\n  ) INTO result_json;\r\n  \r\n  RAISE NOTICE 'Role update completed successfully';\r\n  RETURN result_json;\r\n  \r\nEXCEPTION\r\n  WHEN OTHERS THEN\r\n    RAISE EXCEPTION 'Error updating user roles: %', SQLERRM;\r\nEND;\r\n$function$","CREATE OR REPLACE FUNCTION public.assign_default_role()\n RETURNS trigger\n LANGUAGE plpgsql\n SECURITY DEFINER\n SET search_path TO 'public'\nAS $function$\nDECLARE\n  default_role_id uuid;\nBEGIN\n  -- Get the default role ID\n  SELECT id INTO default_role_id FROM roles WHERE name = 'user' LIMIT 1;\n  \n  IF default_role_id IS NOT NULL THEN\n    -- Insert default role for new user\n    INSERT INTO user_roles (user_id, role_id)\n    VALUES (NEW.user_id, default_role_id)\n    ON CONFLICT (user_id, role_id) DO NOTHING;\n  END IF;\n  \n  RETURN NEW;\nEXCEPTION\n  WHEN OTHERS THEN\n    -- Log error but don't fail profile creation\n    RAISE WARNING 'Error assigning default role to user %: %', NEW.user_id, SQLERRM;\n    RETURN NEW;\nEND;\n$function$","CREATE OR REPLACE FUNCTION public.assign_default_user_role()\n RETURNS trigger\n LANGUAGE plpgsql\n SECURITY DEFINER\n SET search_path TO ''\nAS $function$\nDECLARE\n  user_role_id uuid;\nBEGIN\n  -- Get the id of the 'user' role\n  SELECT id INTO user_role_id FROM public.roles WHERE name = 'user' LIMIT 1;\n  -- Insert into user_roles\n  INSERT INTO public.user_roles (user_id, role_id, assigned_at)\n  VALUES (NEW.user_id, user_role_id, now());\n  RETURN NEW;\nEND;\n$function$","create or replace view \\"public\\".\\"assignment_bookings_view_roster\\" as  SELECT ab.assignment_id,\n    b.booking_id,\n    b.user_id,\n    ((b.first_name || ' '::text) || b.last_name) AS full_name,\n    b.email,\n    ca.status,\n    ca.notes,\n    ca.marked_at\n   FROM ((assignment_bookings ab\n     JOIN bookings b ON ((b.booking_id = ab.booking_id)))\n     LEFT JOIN class_attendance ca ON (((ca.assignment_id = ab.assignment_id) AND (ca.member_id = b.user_id))))","create or replace view \\"public\\".\\"assignments_with_timezone\\" as  SELECT ca.id,\n    ca.scheduled_class_id,\n    ca.instructor_id,\n    ca.assigned_by,\n    ca.payment_amount,\n    ca.payment_status,\n    ca.notes,\n    ca.assigned_at,\n    ca.created_at,\n    ca.updated_at,\n    ca.class_type_id,\n    ca.date,\n    ca.start_time,\n    ca.end_time,\n    ca.schedule_type,\n    ca.class_status,\n    ca.payment_date,\n    ca.instructor_status,\n    ca.instructor_response_at,\n    ca.instructor_remarks,\n    ca.rejection_reason,\n    ca.payment_type,\n    ca.package_id,\n    ca.timezone,\n    ca.created_in_timezone,\n    ca.assignment_method,\n    ca.recurrence_days,\n    ca.parent_assignment_id,\n    ((ca.date + ca.start_time) AT TIME ZONE ca.timezone) AS start_datetime_utc,\n    ((ca.date + ca.end_time) AT TIME ZONE ca.timezone) AS end_datetime_utc,\n    ct.name AS class_type_name,\n    cp.name AS package_name,\n    cp.class_count AS package_class_count,\n    p.full_name AS instructor_name\n   FROM (((class_assignments ca\n     LEFT JOIN class_types ct ON ((ca.class_type_id = ct.id)))\n     LEFT JOIN class_packages cp ON ((ca.package_id = cp.id)))\n     LEFT JOIN profiles p ON ((ca.instructor_id = p.user_id)))","CREATE OR REPLACE FUNCTION public.can_manage_roles()\n RETURNS boolean\n LANGUAGE plpgsql\n SECURITY DEFINER\nAS $function$\nBEGIN\n  RETURN has_role('super_admin');\nEND;\n$function$","CREATE OR REPLACE FUNCTION public.check_admin_access()\n RETURNS boolean\n LANGUAGE plpgsql\n SECURITY DEFINER\nAS $function$\nBEGIN\n  RETURN (\n    -- Check admin_users table (legacy method)\n    EXISTS (SELECT 1 FROM admin_users WHERE email = auth.email())\n    OR \n    -- Check user_roles table (new method)\n    has_role('admin') \n    OR \n    has_role('super_admin')\n  );\nEND;\n$function$","CREATE OR REPLACE FUNCTION public.check_admin_role()\n RETURNS boolean\n LANGUAGE sql\n STABLE SECURITY DEFINER\nAS $function$\r\n  SELECT EXISTS (\r\n    SELECT 1 FROM user_roles ur\r\n    WHERE ur.user_id = auth.uid()\r\n    AND ur.role_id IN (\r\n      SELECT id FROM roles WHERE name IN ('admin', 'super_admin')\r\n    )\r\n  );\r\n$function$","CREATE OR REPLACE FUNCTION public.check_admin_role(check_user_id uuid)\n RETURNS boolean\n LANGUAGE sql\n STABLE SECURITY DEFINER\nAS $function$\n  SELECT EXISTS (\n    SELECT 1 \n    FROM profiles \n    WHERE user_id = check_user_id \n    AND role = 'admin'\n  );\n$function$","CREATE OR REPLACE FUNCTION public.check_can_manage_roles()\n RETURNS boolean\n LANGUAGE plpgsql\n SECURITY DEFINER\nAS $function$\nBEGIN\n  RETURN EXISTS (\n    SELECT 1 FROM user_roles ur\n    JOIN roles r ON ur.role_id = r.id\n    WHERE ur.user_id = auth.uid()\n    AND r.name IN ('admin', 'super_admin')\n  );\nEND;\n$function$","CREATE OR REPLACE FUNCTION public.check_is_admin()\n RETURNS boolean\n LANGUAGE plpgsql\n SECURITY DEFINER\nAS $function$\nBEGIN\n  -- Check if user is authenticated\n  IF auth.uid() IS NULL THEN\n    RETURN false;\n  END IF;\n\n  -- Check admin_users table first\n  IF EXISTS (\n    SELECT 1 FROM admin_users \n    WHERE email = auth.email() \n    AND role IN ('admin', 'super_admin')\n  ) THEN\n    RETURN true;\n  END IF;\n\n  -- Check user_roles table\n  IF EXISTS (\n    SELECT 1 FROM user_roles ur\n    JOIN roles r ON ur.role_id = r.id\n    WHERE ur.user_id = auth.uid()\n    AND r.name IN ('admin', 'super_admin')\n  ) THEN\n    RETURN true;\n  END IF;\n\n  RETURN false;\nEND;\n$function$","CREATE OR REPLACE FUNCTION public.check_user_accounts()\n RETURNS TABLE(total_auth_users integer, total_profiles integer, missing_profiles integer, admin_users integer)\n LANGUAGE plpgsql\n SECURITY DEFINER\nAS $function$\nBEGIN\n  RETURN QUERY\n  SELECT\n    (SELECT COUNT(*) FROM auth.users)::INTEGER,\n    (SELECT COUNT(*) FROM profiles)::INTEGER,\n    (SELECT COUNT(*) FROM auth.users u LEFT JOIN profiles p ON u.id = p.user_id WHERE p.id IS NULL)::INTEGER,\n    (SELECT COUNT(*) FROM admin_users)::INTEGER;\nEND;\n$function$","CREATE OR REPLACE FUNCTION public.check_user_roles()\n RETURNS boolean\n LANGUAGE sql\n SECURITY DEFINER\nAS $function$\n    SELECT EXISTS (\n        SELECT 1 \n        FROM user_roles ur \n        JOIN roles r ON ur.role_id = r.id\n        WHERE ur.user_id = auth.uid() \n        AND r.name = ANY(ARRAY['admin', 'super_admin', 'energy_exchange_lead'])\n    )\n$function$","create or replace view \\"public\\".\\"class_assignment_financials\\" as  SELECT id,\n    instructor_id,\n    date,\n    start_time,\n    end_time,\n    schedule_type,\n    class_status,\n    payment_status,\n    payment_amount,\n    override_payment_amount,\n    COALESCE(override_payment_amount, payment_amount) AS final_payment_amount\n   FROM class_assignments ca","CREATE OR REPLACE FUNCTION public.convert_assignment_to_timezone(assignment_date date, assignment_time time without time zone, stored_timezone text, target_timezone text)\n RETURNS timestamp with time zone\n LANGUAGE plpgsql\nAS $function$\r\nBEGIN\r\n  -- Combine date and time in stored timezone, then convert to target timezone\r\n  RETURN (assignment_date + assignment_time) AT TIME ZONE stored_timezone AT TIME ZONE target_timezone;\r\nEND;\r\n$function$","CREATE OR REPLACE FUNCTION public.create_profile_after_signup()\n RETURNS trigger\n LANGUAGE plpgsql\n SECURITY DEFINER\n SET search_path TO ''\nAS $function$\nBEGIN\n  INSERT INTO public.profiles (user_id)\n  VALUES (NEW.id);\n  RETURN NEW;\nEND;\n$function$","CREATE OR REPLACE FUNCTION public.create_profile_and_role_after_signup()\n RETURNS trigger\n LANGUAGE plpgsql\n SECURITY DEFINER\n SET search_path TO ''\nAS $function$\nDECLARE\n  user_role_id uuid;\nBEGIN\n  -- Insert into profiles\n  INSERT INTO public.profiles (user_id)\n  VALUES (NEW.id);\n\n  -- Get the id of the 'user' role\n  SELECT id INTO user_role_id FROM public.roles WHERE name = 'user' LIMIT 1;\n\n  -- Insert into user_roles\n  IF user_role_id IS NOT NULL THEN\n    INSERT INTO public.user_roles (user_id, role_id)\n    VALUES (NEW.id, user_role_id);\n  END IF;\n\n  RETURN NEW;\nEND;\n$function$","CREATE OR REPLACE FUNCTION public.debug_is_admin()\n RETURNS text\n LANGUAGE plpgsql\n SECURITY DEFINER\nAS $function$\r\nDECLARE \r\n    current_user_id uuid;\r\n    role_count integer;\r\n    user_role_count integer;\r\n    has_admin_role boolean;\r\nBEGIN\r\n    -- Get current user ID\r\n    current_user_id := auth.uid();\r\n    \r\n    -- Check if we can count roles table\r\n    SELECT COUNT(*) INTO role_count FROM roles;\r\n    \r\n    -- Check if we can count user_roles table\r\n    SELECT COUNT(*) INTO user_role_count FROM user_roles;\r\n    \r\n    -- Check if user has admin role\r\n    SELECT EXISTS (\r\n        SELECT 1 \r\n        FROM user_roles ur\r\n        JOIN roles r ON ur.role_id = r.id\r\n        WHERE ur.user_id = current_user_id\r\n        AND r.name IN ('admin', 'super_admin')\r\n    ) INTO has_admin_role;\r\n    \r\n    RETURN format('User ID: %s, Roles count: %s, User_roles count: %s, Has admin: %s', \r\n                  current_user_id, role_count, user_role_count, has_admin_role);\r\n                  \r\nEXCEPTION WHEN OTHERS THEN\r\n    RETURN 'Error: ' || SQLERRM || ' - Detail: ' || SQLSTATE;\r\nEND;\r\n$function$","CREATE OR REPLACE FUNCTION public.debug_user_data()\n RETURNS TABLE(auth_users_count integer, profiles_count integer, user_roles_count integer, admin_users_count integer, missing_profiles_count integer, missing_roles_count integer)\n LANGUAGE plpgsql\n SECURITY DEFINER\nAS $function$\nBEGIN\n  RETURN QUERY\n  SELECT\n    (SELECT COUNT(*) FROM auth.users)::INTEGER AS auth_users_count,\n    (SELECT COUNT(*) FROM profiles)::INTEGER AS profiles_count,\n    (SELECT COUNT(*) FROM user_roles)::INTEGER AS user_roles_count,\n    (SELECT COUNT(*) FROM admin_users)::INTEGER AS admin_users_count,\n    (SELECT COUNT(*) FROM auth.users u LEFT JOIN profiles p ON u.id = p.user_id WHERE p.id IS NULL)::INTEGER AS missing_profiles_count,\n    (SELECT COUNT(*) FROM profiles p LEFT JOIN user_roles ur ON p.user_id = ur.user_id WHERE ur.user_id IS NULL)::INTEGER AS missing_roles_count;\nEND;\n$function$","CREATE OR REPLACE FUNCTION public.diagnose_user_signup()\n RETURNS TABLE(auth_users_count bigint, profiles_count bigint, user_roles_count bigint, users_without_profiles bigint, profiles_without_roles bigint, last_user_email text, last_profile_email text, trigger_exists boolean, profile_policies text[])\n LANGUAGE plpgsql\n SECURITY DEFINER\nAS $function$\nBEGIN\n  RETURN QUERY\n  SELECT\n    (SELECT COUNT(*) FROM auth.users),\n    (SELECT COUNT(*) FROM profiles),\n    (SELECT COUNT(*) FROM user_roles),\n    (SELECT COUNT(*) FROM auth.users u LEFT JOIN profiles p ON u.id = p.user_id WHERE p.id IS NULL),\n    (SELECT COUNT(*) FROM profiles p LEFT JOIN user_roles ur ON p.user_id = ur.user_id WHERE ur.user_id IS NULL),\n    (SELECT email::text FROM auth.users ORDER BY created_at DESC LIMIT 1),\n    (SELECT email::text FROM profiles ORDER BY created_at DESC LIMIT 1),\n    EXISTS (SELECT 1 FROM pg_trigger WHERE tgname = 'on_auth_user_created'),\n    ARRAY(SELECT policyname::text FROM pg_policies WHERE tablename = 'profiles');\nEND;\n$function$","CREATE OR REPLACE FUNCTION public.fix_admin_user()\n RETURNS void\n LANGUAGE plpgsql\n SECURITY DEFINER\nAS $function$\nDECLARE\n    v_admin_email TEXT := 'gourab.master@gmail.com';\n    v_user_id UUID;\n    v_profile_id UUID;\nBEGIN\n    -- Insert or update admin user\n    INSERT INTO admin_users (email, role) \n    VALUES (v_admin_email, 'super_admin')\n    ON CONFLICT (email) DO UPDATE SET\n      role = 'super_admin',\n      updated_at = now();\n      \n    -- Get user ID if exists\n    SELECT id INTO v_user_id \n    FROM auth.users \n    WHERE email = v_admin_email;\n    \n    -- If user exists, ensure they have a profile\n    IF v_user_id IS NOT NULL THEN\n        SELECT id INTO v_profile_id FROM profiles WHERE user_id = v_user_id;\n        \n        IF v_profile_id IS NULL THEN\n            INSERT INTO profiles (user_id, email, full_name, role)\n            VALUES (v_user_id, v_admin_email, 'Admin User', 'admin');\n            RAISE NOTICE 'Created profile for admin user: %', v_admin_email;\n        ELSE\n            UPDATE profiles \n            SET role = 'admin', updated_at = now()\n            WHERE user_id = v_user_id;\n            RAISE NOTICE 'Updated profile for admin user: %', v_admin_email;\n        END IF;\n    END IF;\nEND;\n$function$","CREATE OR REPLACE FUNCTION public.generate_booking_id()\n RETURNS text\n LANGUAGE plpgsql\nAS $function$\r\nDECLARE\r\n    new_id text;\r\n    exists boolean;\r\nBEGIN\r\n    LOOP\r\n        -- Generate booking ID: YOG-YYYYMMDD-XXXX format\r\n        new_id := 'YOG-' || to_char(now(), 'YYYYMMDD') || '-' || LPAD(floor(random() * 10000)::text, 4, '0');\r\n        \r\n        -- Check if this ID already exists\r\n        SELECT EXISTS(SELECT 1 FROM bookings WHERE booking_id = new_id) INTO exists;\r\n        \r\n        -- If unique, exit loop\r\n        IF NOT exists THEN\r\n            EXIT;\r\n        END IF;\r\n    END LOOP;\r\n    \r\n    RETURN new_id;\r\nEND;\r\n$function$","CREATE OR REPLACE FUNCTION public.generate_slug(title text)\n RETURNS text\n LANGUAGE plpgsql\nAS $function$\nBEGIN\n  RETURN lower(\n    regexp_replace(\n      regexp_replace(title, '[^a-zA-Z0-9\\\\s-]', '', 'g'),\n      '\\\\s+', '-', 'g'\n    )\n  );\nEND;\n$function$","CREATE OR REPLACE FUNCTION public.get_booking_details(booking_id_param text)\n RETURNS TABLE(booking_id text, client_name text, client_email text, client_phone text, requested_class text, requested_date text, requested_time text, experience_level text, special_requests text, booking_status text, has_assignment boolean, assignment_date text, assignment_time text, assigned_instructor text)\n LANGUAGE plpgsql\nAS $function$\r\nBEGIN\r\n    RETURN QUERY\r\n    SELECT \r\n        ba.booking_id,\r\n        (ba.first_name || ' ' || ba.last_name) as client_name,\r\n        ba.email as client_email,\r\n        ba.phone as client_phone,\r\n        ba.requested_class,\r\n        ba.requested_date,\r\n        ba.requested_time,\r\n        ba.experience_level,\r\n        ba.special_requests,\r\n        ba.booking_status,\r\n        (ba.assignment_id IS NOT NULL) as has_assignment,\r\n        ba.assigned_date as assignment_date,\r\n        (ba.assigned_start_time || ' - ' || ba.assigned_end_time) as assignment_time,\r\n        ba.assigned_instructor_name as assigned_instructor\r\n    FROM booking_assignments ba\r\n    WHERE ba.booking_id = booking_id_param;\r\nEND;\r\n$function$","CREATE OR REPLACE FUNCTION public.get_highest_user_role(user_id uuid)\n RETURNS text\n LANGUAGE plpgsql\n SECURITY DEFINER\nAS $function$\nDECLARE\n  highest_role text;\nBEGIN\n  -- Priority order: super_admin > admin > instructor > mantra_curator > user\n  SELECT \n    CASE\n      WHEN EXISTS (SELECT 1 FROM user_roles ur JOIN roles r ON ur.role_id = r.id WHERE ur.user_id = $1 AND r.name = 'super_admin') THEN 'super_admin'\n      WHEN EXISTS (SELECT 1 FROM user_roles ur JOIN roles r ON ur.role_id = r.id WHERE ur.user_id = $1 AND r.name = 'admin') THEN 'admin'\n      WHEN EXISTS (SELECT 1 FROM user_roles ur JOIN roles r ON ur.role_id = r.id WHERE ur.user_id = $1 AND r.name = 'instructor') THEN 'instructor'\n      WHEN EXISTS (SELECT 1 FROM user_roles ur JOIN roles r ON ur.role_id = r.id WHERE ur.user_id = $1 AND r.name = 'mantra_curator') THEN 'mantra_curator'\n      WHEN EXISTS (SELECT 1 FROM user_roles ur JOIN roles r ON ur.role_id = r.id WHERE ur.user_id = $1 AND r.name = 'user') THEN 'user'\n      ELSE 'user'\n    END INTO highest_role;\n  \n  RETURN highest_role;\nEND;\n$function$","CREATE OR REPLACE FUNCTION public.get_instructors()\n RETURNS TABLE(user_id uuid, full_name text)\n LANGUAGE plpgsql\nAS $function$\r\nBEGIN\r\n  RETURN QUERY\r\n  SELECT u.id, p.full_name\r\n  FROM users u\r\n  JOIN profiles p ON u.id = p.user_id\r\n  JOIN user_roles ur ON u.id = ur.user_id\r\n  JOIN roles r ON ur.role_id = r.id\r\n  WHERE r.name IN ('instructor', 'yoga_acharya');\r\nEND;\r\n$function$","CREATE OR REPLACE FUNCTION public.get_user_profiles_for_admin()\n RETURNS TABLE(id uuid, user_id uuid, full_name text, phone text, bio text, experience_level text, created_at timestamp with time zone, updated_at timestamp with time zone, email text, user_created_at timestamp with time zone, user_roles text[])\n LANGUAGE plpgsql\n SECURITY DEFINER\nAS $function$\nBEGIN\n  RETURN QUERY\n  SELECT \n    p.id,\n    p.user_id,\n    p.full_name,\n    p.phone,\n    p.bio,\n    get_highest_user_role(p.user_id) as experience_level, -- Use highest role\n    p.created_at,\n    p.updated_at,\n    p.email,\n    u.created_at as user_created_at,\n    ARRAY(\n      SELECT r.name \n      FROM user_roles ur \n      JOIN roles r ON ur.role_id = r.id \n      WHERE ur.user_id = p.user_id\n    ) as user_roles\n  FROM profiles p\n  LEFT JOIN auth.users u ON p.user_id = u.id\n  ORDER BY p.created_at DESC;\nEND;\n$function$","CREATE OR REPLACE FUNCTION public.get_user_roles()\n RETURNS TABLE(role_name text)\n LANGUAGE plpgsql\n SECURITY DEFINER\nAS $function$\nBEGIN\n  RETURN QUERY\n  SELECT r.name\n  FROM user_roles ur\n  JOIN roles r ON ur.role_id = r.id\n  WHERE ur.user_id = auth.uid();\nEND;\n$function$","CREATE OR REPLACE FUNCTION public.get_users_with_roles(role_names text[])\n RETURNS TABLE(id uuid, email text, raw_user_meta_data jsonb, user_roles text[])\n LANGUAGE plpgsql\n SECURITY DEFINER\nAS $function$\nBEGIN\n  RETURN QUERY\n  SELECT \n    u.id,\n    u.email,\n    u.raw_user_meta_data,\n    ARRAY_AGG(r.name) as user_roles\n  FROM auth.users u\n  LEFT JOIN user_roles ur ON u.id = ur.user_id\n  LEFT JOIN roles r ON ur.role_id = r.id\n  WHERE r.name = ANY(role_names)\n  GROUP BY u.id, u.email, u.raw_user_meta_data\n  HAVING COUNT(r.name) > 0;\nEND;\n$function$","CREATE OR REPLACE FUNCTION public.handle_new_user()\n RETURNS trigger\n LANGUAGE plpgsql\n SECURITY DEFINER\n SET search_path TO 'public'\nAS $function$\nBEGIN\n  -- Insert profile directly without checking if it exists\n  -- This is simpler and less error-prone\n  BEGIN\n    INSERT INTO profiles (user_id, email, full_name)\n    VALUES (\n      NEW.id,\n      NEW.email,\n      COALESCE(NEW.raw_user_meta_data->>'full_name', '')\n    );\n    RAISE NOTICE 'Created profile for new user: %', NEW.email;\n  EXCEPTION\n    WHEN unique_violation THEN\n      -- Profile already exists, which is fine\n      RAISE NOTICE 'Profile already exists for user: %', NEW.email;\n    WHEN others THEN\n      -- Log the error but don't block user creation\n      RAISE WARNING 'Error creating profile for user %: %', NEW.email, SQLERRM;\n  END;\n  \n  RETURN NEW;\nEND;\n$function$","CREATE OR REPLACE FUNCTION public.handle_new_user_profile()\n RETURNS trigger\n LANGUAGE plpgsql\n SECURITY DEFINER\n SET search_path TO ''\nAS $function$\nDECLARE\n  user_role_id uuid;\nBEGIN\n  -- Insert into profiles\n  INSERT INTO public.profiles (user_id, email, full_name)\n  VALUES (\n    NEW.id,\n    NEW.email,\n    COALESCE(NEW.raw_user_meta_data->>'full_name', NEW.email)\n  )\n  ON CONFLICT (user_id) DO NOTHING;\n\n  -- Get the id of the 'user' role\n  SELECT id INTO user_role_id FROM public.roles WHERE name = 'user' LIMIT 1;\n\n  -- Insert into user_roles\n  IF user_role_id IS NOT NULL THEN\n    INSERT INTO public.user_roles (user_id, role_id)\n    VALUES (NEW.id, user_role_id)\n    ON CONFLICT DO NOTHING;\n  END IF;\n\n  RETURN NEW;\nEND;\n$function$","CREATE OR REPLACE FUNCTION public.has_role(role_name text)\n RETURNS boolean\n LANGUAGE plpgsql\n SECURITY DEFINER\nAS $function$\r\nBEGIN\r\n  RETURN EXISTS (\r\n    SELECT 1 \r\n    FROM profiles p\r\n    JOIN user_roles ur ON p.user_id = ur.user_id\r\n    JOIN roles r ON ur.role_id = r.id\r\n    WHERE p.user_id = auth.uid() \r\n    AND r.name = role_name\r\n  );\r\nEND;\r\n$function$","create or replace view \\"public\\".\\"instructor_upcoming_classes_v\\" as  SELECT ca.id AS assignment_id,\n    ca.instructor_id,\n    ca.date,\n    ca.start_time,\n    ca.end_time,\n    ca.schedule_type,\n    ca.class_status,\n    ca.payment_status,\n    ca.payment_amount,\n    ca.override_payment_amount,\n    COALESCE(ca.override_payment_amount, ca.payment_amount) AS final_payment_amount,\n    ca.timezone,\n    ca.attendance_locked,\n    COALESCE(sum(((att.status = ANY (ARRAY['present'::attendance_status_enum, 'late'::attendance_status_enum, 'makeup_completed'::attendance_status_enum])))::integer), (0)::bigint) AS present_count,\n    COALESCE(sum(((att.status = 'no_show'::attendance_status_enum))::integer), (0)::bigint) AS no_show_count,\n    COALESCE(avg(NULLIF(cr.rating, 0)), (0)::numeric) AS avg_rating,\n    count(cr.id) AS rating_count\n   FROM ((class_assignments ca\n     LEFT JOIN class_attendance att ON ((att.assignment_id = ca.id)))\n     LEFT JOIN class_ratings cr ON ((cr.assignment_id = ca.id)))\n  WHERE ((ca.date >= CURRENT_DATE) AND (ca.date <= (CURRENT_DATE + '60 days'::interval)))\n  GROUP BY ca.id","CREATE OR REPLACE FUNCTION public.is_admin()\n RETURNS boolean\n LANGUAGE sql\n STABLE\nAS $function$\r\n  SELECT EXISTS (\r\n    SELECT 1\r\n    FROM user_roles ur\r\n    JOIN roles r ON r.id = ur.role_id\r\n    WHERE ur.user_id = auth.uid()\r\n      AND r.name IN ('admin','super_admin')\r\n  );\r\n$function$","CREATE OR REPLACE FUNCTION public.is_admin(uid uuid)\n RETURNS boolean\n LANGUAGE plpgsql\n SECURITY DEFINER\n SET search_path TO 'public', 'pg_temp'\nAS $function$\r\nbegin\r\n  return exists (\r\n    select 1 from public.user_roles ur\r\n    join public.roles r on ur.role_id = r.id\r\n    where ur.user_id = uid\r\n    and r.name in ('admin', 'super_admin')\r\n  );\r\nend;\r\n$function$","CREATE OR REPLACE FUNCTION public.is_admin_or_super_admin()\n RETURNS boolean\n LANGUAGE sql\n SECURITY DEFINER\n SET search_path TO ''\nAS $function$\n  select role in ('admin', 'super admin')\n  from public.profiles\n  where user_id = (select auth.uid());\n$function$","CREATE OR REPLACE FUNCTION public.is_admin_user()\n RETURNS boolean\n LANGUAGE plpgsql\n SECURITY DEFINER\nAS $function$\r\nBEGIN\r\n    -- First try role-based check\r\n    IF EXISTS (\r\n        SELECT 1 \r\n        FROM user_roles ur\r\n        JOIN roles r ON ur.role_id = r.id\r\n        WHERE ur.user_id = auth.uid()\r\n        AND r.name IN ('admin', 'super_admin')\r\n    ) THEN\r\n        RETURN true;\r\n    END IF;\r\n    \r\n    -- Fallback to email check for known admins\r\n    IF (auth.jwt() ->> 'email') = 'gourab.master@gmail.com' THEN\r\n        RETURN true;\r\n    END IF;\r\n    \r\n    RETURN false;\r\nEXCEPTION WHEN OTHERS THEN\r\n    -- If role check fails, try email fallback\r\n    RETURN (auth.jwt() ->> 'email') = 'gourab.master@gmail.com';\r\nEND;\r\n$function$","CREATE OR REPLACE FUNCTION public.is_mantra_curator()\n RETURNS boolean\n LANGUAGE plpgsql\n SECURITY DEFINER\nAS $function$\nBEGIN\n  RETURN has_role('mantra_curator');\nEND;\n$function$","CREATE OR REPLACE FUNCTION public.lock_past_class_attendance()\n RETURNS integer\n LANGUAGE plpgsql\n SECURITY DEFINER\nAS $function$\r\nDECLARE\r\n  v_count integer;\r\nBEGIN\r\n  UPDATE public.class_assignments ca\r\n  SET attendance_locked = true\r\n  WHERE attendance_locked = false\r\n    AND (\r\n      (\r\n        -- Construct naive timestamp (date + end_time) then interpret in stored timezone\r\n        ((ca.date::text || ' ' || ca.end_time::text)::timestamp AT TIME ZONE ca.timezone)\r\n        + INTERVAL '30 minutes'\r\n      ) < now()\r\n    );\r\n  GET DIAGNOSTICS v_count = ROW_COUNT;\r\n  RETURN v_count;\r\nEND;\r\n$function$","CREATE OR REPLACE FUNCTION public.promote_from_waitlist()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\nDECLARE\n  waitlist_entry RECORD;\nBEGIN\n  -- Only run when a booking is cancelled\n  IF TG_OP = 'UPDATE' AND OLD.booking_status = 'confirmed' AND NEW.booking_status = 'cancelled' THEN\n    -- Get the first person on the waitlist for this class\n    SELECT * INTO waitlist_entry\n    FROM waitlist\n    WHERE scheduled_class_id = NEW.scheduled_class_id\n    ORDER BY position ASC\n    LIMIT 1;\n    \n    IF FOUND THEN\n      -- Create a booking for the waitlisted person\n      INSERT INTO class_bookings (\n        user_id,\n        scheduled_class_id,\n        first_name,\n        last_name,\n        email,\n        phone,\n        booking_status\n      ) VALUES (\n        waitlist_entry.user_id,\n        waitlist_entry.scheduled_class_id,\n        'Waitlist',\n        'User',\n        waitlist_entry.email,\n        waitlist_entry.phone,\n        'confirmed'\n      );\n      \n      -- Remove from waitlist\n      DELETE FROM waitlist WHERE id = waitlist_entry.id;\n      \n      -- Update positions for remaining waitlist entries\n      UPDATE waitlist \n      SET position = position - 1\n      WHERE scheduled_class_id = waitlist_entry.scheduled_class_id\n        AND position > waitlist_entry.position;\n    END IF;\n  END IF;\n  \n  RETURN NEW;\nEND;\n$function$","CREATE OR REPLACE FUNCTION public.remove_admin_user()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\nbegin\n  if old.role in ('super_admin', 'admin') and new.role not in ('super_admin', 'admin') then\n    delete from public.admin_users where user_id = old.user_id;\n  end if;\n  return new;\nend;\n$function$","CREATE OR REPLACE FUNCTION public.set_article_author()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\nBEGIN\n  IF NEW.author_id IS NULL THEN\n    NEW.author_id = auth.uid();\n  END IF;\n  RETURN NEW;\nEND;\n$function$","CREATE OR REPLACE FUNCTION public.set_booking_id()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\r\nBEGIN\r\n    IF NEW.booking_id IS NULL THEN\r\n        NEW.booking_id := generate_booking_id();\r\n    END IF;\r\n    RETURN NEW;\r\nEND;\r\n$function$","CREATE OR REPLACE FUNCTION public.set_class_attendance_updated_at()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\r\nBEGIN\r\n  NEW.updated_at = now();\r\n  RETURN NEW;\r\nEND;\r\n$function$","CREATE OR REPLACE FUNCTION public.set_class_ratings_updated_at()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\r\nBEGIN\r\n  NEW.updated_at = now();\r\n  RETURN NEW;\r\nEND;\r\n$function$","CREATE OR REPLACE FUNCTION public.set_contact_message_user_id()\n RETURNS trigger\n LANGUAGE plpgsql\n SECURITY DEFINER\nAS $function$\nBEGIN\n    IF auth.uid() IS NOT NULL THEN\n        NEW.user_id = auth.uid();\n    END IF;\n    RETURN NEW;\nEND;\n$function$","CREATE OR REPLACE FUNCTION public.sync_admin_users()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\nbegin\n  -- On INSERT: Add to admin_users if role is admin/super_admin and not already present\n  if tg_op = 'INSERT' then\n    if exists (\n      select 1 from public.roles r where r.id = new.role_id and r.name in ('admin', 'super_admin')\n    ) then\n      insert into public.admin_users (id, email)\n      select new.user_id, p.email\n      from public.profiles p\n      where p.user_id = new.user_id\n        and not exists (\n          select 1 from public.admin_users au where au.id = new.user_id\n        );\n    end if;\n    return new;\n  end if;\n\n  -- On UPDATE: Handle role changes\n  if tg_op = 'UPDATE' then\n    -- If role changed to admin/super_admin, add to admin_users if not present\n    if exists (\n      select 1 from public.roles r where r.id = new.role_id and r.name in ('admin', 'super_admin')\n    ) and not exists (\n      select 1 from public.roles r where r.id = old.role_id and r.name in ('admin', 'super_admin')\n    ) then\n      insert into public.admin_users (id, email)\n      select new.user_id, p.email\n      from public.profiles p\n      where p.user_id = new.user_id\n        and not exists (\n          select 1 from public.admin_users au where au.id = new.user_id\n        );\n    end if;\n    -- If role changed from admin/super_admin to non-admin, remove if no more admin roles\n    if exists (\n      select 1 from public.roles r where r.id = old.role_id and r.name in ('admin', 'super_admin')\n    ) and not exists (\n      select 1 from public.roles r where r.id = new.role_id and r.name in ('admin', 'super_admin')\n    ) then\n      if not exists (\n        select 1 from public.user_roles ur\n        join public.roles r on ur.role_id = r.id\n        where ur.user_id = old.user_id and r.name in ('admin', 'super_admin')\n      ) then\n        delete from public.admin_users where id = old.user_id;\n      end if;\n    end if;\n    return new;\n  end if;\n\n  -- On DELETE: Remove from admin_users if user has no more admin/super_admin roles\n  if tg_op = 'DELETE' then\n    if exists (\n      select 1 from public.roles r where r.id = old.role_id and r.name in ('admin', 'super_admin')\n    ) then\n      if not exists (\n        select 1 from public.user_roles ur\n        join public.roles r on ur.role_id = r.id\n        where ur.user_id = old.user_id and r.name in ('admin', 'super_admin')\n      ) then\n        delete from public.admin_users where id = old.user_id;\n      end if;\n    end if;\n    return old;\n  end if;\n\n  return null;\nend;\n$function$","CREATE OR REPLACE FUNCTION public.sync_missing_profiles()\n RETURNS void\n LANGUAGE plpgsql\n SECURITY DEFINER\nAS $function$\nDECLARE\n    v_user_record RECORD;\n    v_count INTEGER := 0;\nBEGIN\n    FOR v_user_record IN \n        SELECT \n            u.id, \n            u.email, \n            u.raw_user_meta_data->>'full_name' as full_name\n        FROM \n            auth.users u\n        LEFT JOIN \n            profiles p ON u.id = p.user_id\n        WHERE \n            p.id IS NULL\n    LOOP\n        BEGIN\n            INSERT INTO profiles (\n                user_id, \n                email, \n                full_name\n            ) \n            VALUES (\n                v_user_record.id, \n                v_user_record.email, \n                COALESCE(v_user_record.full_name, v_user_record.email)\n            );\n            v_count := v_count + 1;\n        EXCEPTION WHEN others THEN\n            RAISE WARNING 'Error syncing profile for user %: %', v_user_record.id, SQLERRM;\n        END;\n    END LOOP;\n    \n    RAISE NOTICE 'Created % missing profile(s)', v_count;\nEND;\n$function$","create or replace view \\"public\\".\\"transactions_with_user\\" as  SELECT t.id,\n    t.user_id,\n    t.subscription_id,\n    t.amount,\n    t.currency,\n    t.status,\n    t.payment_method,\n    t.stripe_payment_intent_id,\n    t.description,\n    t.created_at,\n    t.updated_at,\n    t.billing_plan_type,\n    t.billing_period_month,\n    t.category,\n    u.email AS user_email,\n    COALESCE((u.raw_user_meta_data ->> 'full_name'::text), split_part((u.email)::text, '@'::text, 1)) AS user_full_name\n   FROM (transactions t\n     LEFT JOIN auth.users u ON ((u.id = t.user_id)))","CREATE OR REPLACE FUNCTION public.trg_add_to_admin_users()\n RETURNS trigger\n LANGUAGE plpgsql\n SECURITY DEFINER\n SET search_path TO ''\nAS $function$\r\nbegin\r\n  if new.role in ('admin', 'super_admin') then\r\n    insert into admin_users (id)\r\n    values (new.user_id)\r\n    on conflict (id) do nothing;\r\n  end if;\r\n  return new;\r\nend;\r\n$function$","CREATE OR REPLACE FUNCTION public.uid()\n RETURNS uuid\n LANGUAGE sql\n STABLE\nAS $function$\n  SELECT auth.uid();\n$function$","CREATE OR REPLACE FUNCTION public.update_article_view_count()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\nBEGIN\n  UPDATE articles \n  SET view_count = (\n    SELECT COUNT(*) \n    FROM article_views \n    WHERE article_id = NEW.article_id\n  )\n  WHERE id = NEW.article_id;\n  RETURN NEW;\nEND;\n$function$","CREATE OR REPLACE FUNCTION public.update_class_assignments_updated_at()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\nBEGIN\n  NEW.updated_at = now();\n  RETURN NEW;\nEND;\n$function$","CREATE OR REPLACE FUNCTION public.update_class_bookings_updated_at()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\nBEGIN\n  NEW.updated_at = now();\n  RETURN NEW;\nEND;\n$function$","CREATE OR REPLACE FUNCTION public.update_class_packages_updated_at()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\nBEGIN\n  NEW.updated_at = now();\n  RETURN NEW;\nEND;\n$function$","CREATE OR REPLACE FUNCTION public.update_class_participant_count()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\nBEGIN\n  IF TG_OP = 'INSERT' THEN\n    -- Increase count for new confirmed booking\n    IF NEW.booking_status = 'confirmed' THEN\n      UPDATE scheduled_classes \n      SET current_participants = current_participants + 1\n      WHERE id = NEW.scheduled_class_id;\n    END IF;\n    RETURN NEW;\n  ELSIF TG_OP = 'UPDATE' THEN\n    -- Handle status changes\n    IF OLD.booking_status = 'confirmed' AND NEW.booking_status != 'confirmed' THEN\n      -- Booking was cancelled or changed from confirmed\n      UPDATE scheduled_classes \n      SET current_participants = current_participants - 1\n      WHERE id = NEW.scheduled_class_id;\n    ELSIF OLD.booking_status != 'confirmed' AND NEW.booking_status = 'confirmed' THEN\n      -- Booking was confirmed\n      UPDATE scheduled_classes \n      SET current_participants = current_participants + 1\n      WHERE id = NEW.scheduled_class_id;\n    END IF;\n    RETURN NEW;\n  ELSIF TG_OP = 'DELETE' THEN\n    -- Decrease count for deleted confirmed booking\n    IF OLD.booking_status = 'confirmed' THEN\n      UPDATE scheduled_classes \n      SET current_participants = current_participants - 1\n      WHERE id = OLD.scheduled_class_id;\n    END IF;\n    RETURN OLD;\n  END IF;\n  RETURN NULL;\nEND;\n$function$","CREATE OR REPLACE FUNCTION public.update_instructor_availability_updated_at()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\nBEGIN\n  NEW.updated_at = now();\n  RETURN NEW;\nEND;\n$function$","CREATE OR REPLACE FUNCTION public.update_modified_column()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\r\nBEGIN\r\n    NEW.updated_at = now();\r\n    RETURN NEW;\r\nEND;\r\n$function$","CREATE OR REPLACE FUNCTION public.update_newsletters_updated_at()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\nBEGIN\n  NEW.updated_at = now();\n  RETURN NEW;\nEND;\n$function$","CREATE OR REPLACE FUNCTION public.update_roles_updated_at()\n RETURNS trigger\n LANGUAGE plpgsql\nAS $function$\nBEGIN\n  N